searchProducts:
query searchProducts($first: Int!, $after: String, $sort: StoreSort, $term: String, $selectedFacets: [IStoreSelectedFacet!]) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    suggestions {
      terms {
        value
      }
      products {
        slug
        name
        image {
          url
          alternateName
        }
        brand {
          name
        }
        description
        offers {
          highPrice
          lowPrice
          offerCount
          priceCurrency
          offers {
            listPrice
            sellingPrice
            priceCurrency
            price
            priceValidUntil
            itemCondition
            availability
            seller {
              identifier
            }
          }
        }
      }
    }
  }
}
