getCollection:
query getCollection($first: Int!, $after: String) {
  allCollections(first: $first, after: $after) {
    pageInfo {
      hasNextPage
      hasPreviousPage
      startCursor
      endCursor
      totalCount
      __typename
    }
    edges {
      node {
        seo {
          title
          titleTemplate
          description
          canonical
        }
        breadcrumbList {
          itemListElement {
            item
            name
            position
          }
          numberOfItems
        }
        slug
        type
      }
    }
  }
}
