ValidateCart:
mutation ValidateCart($cart: IStoreCart!, $session: IStoreSession!) {
  validateCart(cart: $cart, session: $session) {
    order {
      orderNumber
      acceptedOffer {
        seller {
          identifier
        }
        quantity
        price
        listPrice
        itemOffered {
          sku
          name
          image {
            url
            alternateName
          }
          brand {
            name
          }
          isVariantOf {
            productGroupID
            name
          }
          gtin
          additionalProperty {
            propertyID
            name
            value
            valueReference
          }
        }
      }
    }
    messages {
      text
      status
    }
  }
}

ValidateSession:
mutation ValidateSession($session: IStoreSession!, $search: String!) {
  validateSession(session: $session, search: $search) {
    locale
    channel
    country
    postalCode
    currency {
      code
      symbol
    }
    person {
      id
      email
      givenName
      familyName
    }
  }
}

ProductPage:
query ProductPage($locator: [IStoreSelectedFacet!]!) {
  product(locator: $locator) {
    id: productID
    sku
    name
    gtin
    description
    isVariantOf {
      name
      productGroupID
      skuVariants {
        activeVariations
        slugsMap(dominantVariantName: "Color")
        availableVariations(dominantVariantName: "Color")
      }
    }
    image {
      url
      alternateName
    }
    brand {
      name
    }
    offers {
      lowPrice
      offers {
        availability
        price
        listPrice
        seller {
          identifier
        }
      }
    }
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    additionalProperty {
      propertyID
      name
      value
      valueReference
    }
  }
}

Products:
query Products($first: Int!, $after: String, $sort: StoreSort!, $selectedFacets: [IStoreSelectedFacet!]!) {
  search(
    first: $first
    after: $after
    sort: $sort
    selectedFacets: $selectedFacets
  ) {
    products {
      pageInfo {
        totalCount
      }
      edges {
        node {
          id: productID
          slug
          sku
          brand {
            brandName: name
            name
          }
          name
          gtin
          isVariantOf {
            productGroupID
            name
          }
          image {
            url
            alternateName
          }
          offers {
            lowPrice
            offers {
              availability
              price
              listPrice
              quantity
              seller {
                identifier
              }
            }
          }
          additionalProperty {
            propertyID
            name
            value
            valueReference
          }
        }
      }
    }
  }
}

SearchSuggestions:
query SearchSuggestions($first: Int!, $after: String, $sort: StoreSort, $term: String, $selectedFacets: [IStoreSelectedFacet!]) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    suggestions {
      terms {
        value
      }
      products {
        slug
        name
        image {
          url
          alternateName
        }
        brand {
          name
        }
        description
        isVariantOf {
          productGroupID
          name
        }
        offers {
          highPrice
          lowPrice
          offerCount
          priceCurrency
          offers {
            listPrice
            sellingPrice
            priceCurrency
            price
            priceValidUntil
            itemCondition
            availability
            seller {
              identifier
            }
          }
        }
      }
    }
  }
}
